#include <iostream>#include <thread>using namespace std;void myThreadFn( int tId ){    for( int i=0; i<10; i++ ){        cout << "Nit " << tId << " : " << i << " : ";		for( unsigned i=0; i<40000; i++ )			if( i%1000 == 0 )				cout << tId;		cout << "\n";	}}int main(int argc, char **argv){    cout << "Pripremi i kreni..." << endl;	thread t1( myThreadFn, 1 );	thread t2( myThreadFn, 2 );	cout << "...sacekaj..." << endl;    t1.join();    t2.join();    cout << "Kraj." << endl;    return 0;}