#include <iostream>#include <thread>#include <vector>using namespace std;int globalData = 0;void myThreadFn( int tId ){    for( int i=0; i<10; i++ ){		int x = globalData;        cout << "Nit " << tId << " : " << i << endl;        x++;		globalData = x;    }}int main(int argc, char **argv){    int n = 100;    vector<thread*> threads;    cout << "Pripremi i kreni..." << endl;    for( int i=0; i<n; i++ )        threads.push_back( new thread( myThreadFn, i ));	cout << "...sacekaj..." << endl;    for( thread* t: threads )        t->join();    cout << "Kraj." << endl;    for( thread* t: threads )        delete t;    threads.clear();    cout << "globalData = " << globalData << endl;    return 0;}